describe("Creating custom map controls",function(){var a;beforeEach(function(){a=a||new GMaps({el:"#basic-map",lat:-12.0433,lng:-77.0283,zoom:12})});it("should add default styles for the control",function(){a.addControl({position:"top_right",content:"Geolocate"});expect(a.controls[0].position).toEqual(google.maps.ControlPosition.TOP_RIGHT);expect(a.controls[0].style.fontFamily).not.toEqual("")});it("should leave off default styles if requested",function(){a.addControl({position:"top_right",disableDefaultStyles:true,content:'<i class="icon"></i>'});expect(a.controls[1].position).toEqual(google.maps.ControlPosition.TOP_RIGHT);expect(a.map.controls[google.maps.ControlPosition.TOP_RIGHT].length).toEqual(2);expect(a.controls[1].style.fontFamily).toEqual("")});it("should remove control",function(){var b=a.controls[0];a.removeControl(b);expect(a.controls.length).toEqual(1);expect(a.map.controls[google.maps.ControlPosition.TOP_RIGHT].length).toEqual(1);expect(a.controls[0]).not.toEqual(b)})});